page.script:
sceneReturnObj = {
	//# sourceURL=dynamicScript.js

	//Loads a content panel with a number input up to 60 minutes
	//When a user selects a number and hits OK, then that ammount of time is added
	// and text appears confirming the change
	// the next button takes you back to the hallway
	initPageScript : function(){
		$("#pageContent").html("<div>1 minute</div>" + 
									"<input type='range' id='ff_mins' value='30' min='1' max='60'>" +
									"<div>60 minutes</div>" + 
									"<input type='submit' onclick='sceneReturnObj.Hallway_pg_fast_forward_submit()'>")
	},

	Hallway_pg_fast_forward_submit : function(){
		var ff_mins = $("#ff_mins").attr("value")

		if(ff_mins < 10){
			ff_mins = "0" + ff_mins
		}

		addDurationToCurrentTime("0000-00-00 00:" + ff_mins  + ":00")

		//Check if this decision should be displayed
		var output = "<p>It worked!</p>"

	    output += "<div class='decisionBtn' onclick='loadScene(\"Hallway\")' >Next Page</div><br />";

		//output page
		$("body #pageContent").html(output);
	}
}
